#include <iostream>
#include <random>
using namespace std;int get_random_number(int min, int max) {random_device rd;mt19937 gen(rd());uniform_int_distribution<> dis(min, max);return dis(gen);}int main() {system("chcp 65001");while (1) {int min_num = 1;int max_num = 100;int random_num = get_random_number(min_num, max_num);int user_num = 0;int count = 1;do {cout << count << ".请输入数字(" << min_num << "~" << max_num << "): ";if (!(cin >> user_num) || user_num < min_num || user_num > max_num) {cout << "输入有误，请重新输入" << endl;cin.clear();cin.ignore(numeric_limits<streamsize>::max(), '\n');continue;}if (user_num < random_num) {cout << "猜小了" << endl;min_num = user_num;count++;continue;} else if (user_num > random_num) {cout << "猜大了" << endl;max_num = user_num;count++;continue;} else if (user_num == random_num) {cout << "猜中了, boom!!!" << endl;break;}} while (user_num!= random_num);cout << "你一共猜了" << count << "次" << endl;while (1) {int user_input;cout << "游戏结束，按 1 开始下一局游戏，按 2 退出：";if (!(cin >> user_input) || user_input!= 1 && user_input!= 2) {cout << "输入有误，请重新输入" << endl;cin.clear();cin.ignore(numeric_limits<streamsize>::max(), '\n');continue;} else if (user_input == 1) {break;} else if (user_input == 2) {return 0;}}}}